	public final Company save() throws SQLException {
		String username = createUsername();
		String password = createPassword();
		String[] data = new String[] {username, password, name, address, Integer.toString(zip), town, mail};

		Connection con = ConnectionHandler.getConnection();
		
		PreparedStatement stm;
		stm = con.prepareStatement("INSERT INTO `company`(`username`, `password`, `name`, `address`, `zip`, `town`, `mail`, `activation`) VALUES (?,?,?,?,?,?,?,?)");
		stm.setString(1, username);
		stm.setString(2, hash(password));
		stm.setString(3, name);
		stm.setString(4, address);
		stm.setInt(5, zip);
		stm.setString(6, town);
		stm.setString(7, mail);
		//TODO: check if delete
		stm.setString(8, "");
		stm.execute();
		sendLoginData(data);
		return this;
	}
	
	
	private final void sendLoginData(String[] data) {
		new MailService(data);
		// TODO: implement login data sending
	}

	@Override
	public int hashCode() {
		final int prime = 31;
		int result = 1;
		result = prime * result + ((address == null) ? 0 : address.hashCode());
		result = prime * result + id;
		result = prime * result + ((mail == null) ? 0 : mail.hashCode());
		result = prime * result + ((name == null) ? 0 : name.hashCode());
		result = prime * result + ((password == null) ? 0 : password.hashCode());
		result = prime * result + ((town == null) ? 0 : town.hashCode());
		result = prime * result + ((username == null) ? 0 : username.hashCode());
		result = prime * result + zip;
		return result;
	}

	@Override
	public boolean equals(Object obj) {
		if (this == obj) return true;
		if (obj == null) return false;
		if (getClass() != obj.getClass()) return false;
		Company other = (Company) obj;
		if (address == null) {
			if (other.address != null)
				return false;
		} else if (!address.equals(other.address))
			return false;
		if (id != other.id)
			return false;
		if (mail == null) {
			if (other.mail != null)
				return false;
		} else if (!mail.equals(other.mail))
			return false;
		if (name == null) {
			if (other.name != null)
				return false;
		} else if (!name.equals(other.name))
			return false;
		if (password == null) {
			if (other.password != null)
				return false;
		} else if (!password.equals(other.password))
			return false;
		if (town == null) {
			if (other.town != null)
				return false;
		} else if (!town.equals(other.town))
			return false;
		if (username == null) {
			if (other.username != null)
				return false;
		} else if (!username.equals(other.username))
			return false;
		if (zip != other.zip)
			return false;
		return true;
	}
	
	private static String hash(String s) {
		byte[] data = null;
		try {
			data = MessageDigest.getInstance("SHA-256").digest(s.getBytes());
		} catch (NoSuchAlgorithmException e) {
			data = s.getBytes();
		}
		return new String(data);
	}
	
	private static final boolean MxLookup(String mail) {
		String[] temp = mail.split("@");
		String hostname = temp[1];
		Hashtable<String, String> env = new Hashtable<String, String>();
		
		env.put("java.naming.factory.initial",
				"com.sun.jndi.dns.DnsContextFactory");
		try {
			DirContext ictx = new InitialDirContext(env);
			Attributes attrs = ictx.getAttributes(hostname, new String[] {"MX"});
			Attribute attr = attrs.get("MX");
			if (attr == null) {
				return false;
			} else {
				return true;
			}
		} catch (NamingException e) {
			return false;
		}
	}
	
	private static final boolean validatePlz(int zip) {
		URL url;
		HttpURLConnection conn;
		BufferedReader rd;
		String line;
		try {
			url = new URL("http://www.post.ch/db/owa/pv_plz_pack/pr_check_data?p_language=de&p_nap="+zip+"&p_localita=&p_cantone=&p_tipo=luogo");
			conn = (HttpURLConnection) url.openConnection();
			conn.setRequestMethod("GET");
			rd = new BufferedReader(new InputStreamReader(conn.getInputStream()));
			while ((line = rd.readLine()) != null) {
				if(line.contains("Keine PLZ gefunden"))
					return false;
			}
			rd.close();
                        return true;
		} catch (Exception e) {
			e.printStackTrace();
		}
		return false;
	}

	private static final String validatePassword(String pw) {
		String error = null;
		if (pw != null) {
			if (pw.trim().length() < 8) {
				error = "Passwort zu kurz (min. 8 Zeichen).";
			} else if (pw.trim().length() > 64) {
				error ="Passwort zu lang (max. 64 Zeichen).";
			} else if (!pw.matches("[èéÈÉäöüÄÖÜß\\-\\_\\.\\w]+")) {
	    		error = "Ungültige Zeichen im Passwort.";
	    	}
		}
		return error;
	}
	
	private static final String createPassword() {
		SecureRandom random = new SecureRandom();
	    return new BigInteger(130, random).toString(32);
	}
	
	private static final String createUsername() {
		SecureRandom random = new SecureRandom();
	    return new BigInteger(130, random).toString(32);
	}
